mapper.select:
SELECT TALENT_NUM, TITLE, CATEGORY1, CATEGORY2, AREA1, AREA2, WRITER, PAY FROM( SELECT RNUM, TALENT_NUM, TITLE, CATEGORY1, CATEGORY2, AREA1, AREA2, WRITER, PAY FROM( SELECT TALENT_NUM, TITLE, CATEGORY1, CATEGORY2, AREA1, AREA2, WRITER, PAY, ROWNUM AS RNUM FROM( SELECT * FROM TALENT_VIEW
AND AREA1_NUM = #{area1_num}
AND AREA2_NUM = #{area2_num}
AND CATEGORY1_NUM = #{category1_num}
AND CATEGORY2_NUM = #{category2_num}
AND TITLE LIKE '%' || #{keyword} || '%'
AND CONTENT LIKE '%' || #{keyword} || '%'
AND WRITER LIKE '%' || #{keyword} || '%'
ORDER BY TALENT_NUM DESC ) ) WHERE RNUM >= #{startList} ) WHERE ROWNUM <= #{listSize}

SELECT
COUNT(*) AS listCnt
FROM
TALENT_VIEW


		AND AREA1_NUM = #{area1_num}


		AND AREA2_NUM = #{area2_num}


		AND CATEGORY1_NUM = #{category1_num}


		AND CATEGORY2_NUM = #{category2_num}


		AND TITLE LIKE '%' || #{keyword} || '%'


		AND CONTENT LIKE '%' || #{keyword} || '%'


		AND WRITER LIKE '%' || #{keyword} || '%'
